import http from "./http-common";

var getAll = url => {
  return http.get(url);
};

var get = (url, id) => {
  return http.get("".concat(url, "/").concat(id));
};

var create = (url, data) => {
  return http.post(url, data);
};

var update = (url, id, data) => {
  return http.put("".concat(url, "/").concat(id), data);
};

var remove = (url, id) => {
  return http.delete("".concat(url, "/").concat(id));
};

export default {
  getAll,
  get,
  create,
  update,
  remove
};
