import { h } from "preact";
import { useState, useEffect } from "preact/hooks/";
import DataService from "../../service/service";
import Select from "react-select";
import { useRouter } from "next/router";

interface CampaignI {
  id?: string;
  name: string;
  imageMain: string;
  productWebsite: string;
  productValue: string;
  productCategory: string;
  productCategoryValue: string;
  productSend: string;
  productInfluencerCaption: string;
  productPostStyle: string;
  styleGuides: string[];
  firstPostLooking: string;
  instagramStoryRequire: boolean;
  targetStates: string[];
  gender: string[];
  instagramHandles: string[];
  hashtags: string[];
  vipCreator: boolean;
  productFolder: string;
}

const selectSingleStyles = {
  control: (styles: any) => ({
    ...styles,
    borderRadius: 0,
    border: "1px solid #e3a008",
    padding: "0.75rem",
    backgroundColor: "rgba(22, 30, 46, var(--bg-opacity))",
  }),
  option: (styles: any) => {
    const color = "white";
    return {
      ...styles,
      color: "black",
      backgroundColor: color,
      ":hover": {
        ...styles[":hover"],
        color: "white",
        cursor: "pointer",
        backgroundColor: "rgba(22, 30, 46, var(--bg-opacity))",
      },
      ":active": {
        ...styles[":active"],
        backgroundColor: "white",
      },
    };
  },
  singleValue: (styles: any) => ({ ...styles, color: "white" }),
};

const selectMultiStyles = {
  control: (styles: any) => ({
    ...styles,
    borderRadius: 0,
    border: "1px solid #e3a008",
    padding: "0.75rem",
    backgroundColor: "rgba(22, 30, 46, var(--bg-opacity))",
  }),
  option: (styles: any) => {
    const color = "white";
    return {
      ...styles,
      color: "black",
      backgroundColor: color,
      ":hover": {
        ...styles[":hover"],
        color: "white",
        cursor: "pointer",
        backgroundColor: "rgba(22, 30, 46, var(--bg-opacity))",
      },
      ":active": {
        ...styles[":active"],
        backgroundColor: "white",
      },
    };
  },
  multiValue: (styles: any) => {
    const color = "#e3a008";
    return {
      ...styles,
      padding: "0.3rem",
      backgroundColor: color,
      borderRadius: 0,
    };
  },
  multiValueLabel: (styles: any) => ({
    ...styles,
  }),
  multiValueRemove: (styles: any) => ({
    ...styles,
    color: "black",
    ":hover": {
      color: "black",
      cursor: "pointer",
    },
  }),
};

const Editcapaign = () => {
  let queryString;
  if (typeof window !== "undefined") {
    queryString = window.location.search;
  }
  const urlParams = new URLSearchParams(queryString);
  const id: string = urlParams.get("id") || "";

  const router = useRouter();

  const targetStateOption = [
    { value: "all", label: "All" },
    { value: "food", label: "Food" },
  ];

  const genderOption = [
    { value: "any", label: "Any" },
    { value: "male", label: "Male" },
    { value: "female", label: "Female" },
  ];

  const [campaign, setCampaign] = useState<CampaignI>({
    name: "",
    imageMain: "",
    productWebsite: "",
    productValue: "",
    productCategory: "",
    productCategoryValue: "",
    productSend: "",
    productInfluencerCaption: "",
    productPostStyle: "",
    styleGuides: [],
    firstPostLooking: "image",
    instagramStoryRequire: false,
    targetStates: [],
    gender: [],
    instagramHandles: [],
    hashtags: [],
    vipCreator: false,
    productFolder: "",
  });

  const getCampiagn = () => {
    if (id) {
      DataService.get("/campaigns", id).then((res) => {
        setCampaign(res.data);
        // console.log(campaign);

        const image_button = document.getElementById("image-post");
        const video_button = document.getElementById("video-post");
        if (res.data.firstPostLooking) {
          image_button?.classList.remove("bg-white", "text-gray-700");
          image_button?.classList.add("text-white");
        } else {
          video_button?.classList.remove("bg-white", "text-gray-700");
          video_button?.classList.add("text-white");
        }

